mapper.select:


select no, name , role
from user 
where email=#{email } and password=#{password }



select no, name 
from user 
where email=#{email } and password=#{password }

mapper.update:

update user
	set name =#{name },


			
		password=#{password },
		


		gender=#{gender }
	where no = #{no }

mapper.select:


select no, name , email , gender
from user 
where no = #{no }



select no,email
from user 
where email = #{email }
